package com.nganhthien.mikemovie.screen.home;


import android.os.Bundle;
import android.support.annotation.NonNull;
import android.support.annotation.Nullable;
import android.support.v4.app.Fragment;
import android.util.Log;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;

import com.bumptech.glide.Glide;
import com.nganhthien.mikemovie.R;
import com.nganhthien.mikemovie.data.model.Movie;
import com.nganhthien.mikemovie.data.repository.MovieRepository;

import java.util.List;

import static com.nganhthien.mikemovie.utils.Constants.SpecialMovieList.SPECIAL_MOVIE_LIST_MOST_POPULAR;
import static com.nganhthien.mikemovie.utils.Constants.SpecialMovieList.SPECIAL_MOVIE_LIST_NOW_PLAYING;
import static com.nganhthien.mikemovie.utils.Constants.SpecialMovieList.SPECIAL_MOVIE_LIST_TOP_RATE;
import static com.nganhthien.mikemovie.utils.Constants.SpecialMovieList.SPECIAL_MOVIE_LIST_UPCOMING;

/**
 * A simple {@link Fragment} subclass.
 */
public class HomeMovieSpecialListFragment extends Fragment
        implements HomeMovieSpecialListContract.View{

    private static final String ARGUMENT_PAGE = "ARGUMENT_PAGE";
    private HomeMovieSpecialListContract.Presenter mPresenter;
    private TextView mTextTitle;
    private TextView mTextHeading;
    private TextView mTextDescription;
    private TextView mTextSubHeading1;
    private TextView mTextSubHeading2;
    private TextView mTextSubHeading3;
    private ImageView mImage1;
    private ImageView mImage2;
    private ImageView mImage3;
    private ImageView mImage4;
    private ImageView mImage5;

    public static HomeMovieSpecialListFragment newInstance(String type) {
        HomeMovieSpecialListFragment homeMovieSpecialListFragment = new HomeMovieSpecialListFragment();
        Bundle bundle = new Bundle();
        bundle.putString(ARGUMENT_PAGE, type);
        homeMovieSpecialListFragment.setArguments(bundle);
        return homeMovieSpecialListFragment;
    }

    public HomeMovieSpecialListFragment() {
        // Required empty public constructor
    }

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        mPresenter = new HomeMovieSpecialListPresenter(MovieRepository.getInstance());
        mPresenter.setView(this);
        mPresenter.loadMovieSpecialList(SPECIAL_MOVIE_LIST_MOST_POPULAR);
        mPresenter.loadMovieSpecialList(SPECIAL_MOVIE_LIST_NOW_PLAYING);
        mPresenter.loadMovieSpecialList(SPECIAL_MOVIE_LIST_TOP_RATE);
        mPresenter.loadMovieSpecialList(SPECIAL_MOVIE_LIST_UPCOMING);
        // Inflate the layout for this fragment
        return inflater.inflate(R.layout.fragment_movie_special_list_item, container, false);
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        mTextTitle = view.findViewById(R.id.text_head_movie_special_list);

        mTextHeading = view.findViewById(R.id.text_title_movie_special_list);
        mTextDescription = view.findViewById(R.id.text_description_movie_special_list);
        mTextSubHeading1 = view.findViewById(R.id.text_sub_1_movie_special_list);
        mTextSubHeading2 = view.findViewById(R.id.text_sub_2_movie_special_list);
        mTextSubHeading3 = view.findViewById(R.id.text_sub_3_movie_special_list);

        mImage1 = view.findViewById(R.id.image_view_movie_special_list);
        mImage2 = view.findViewById(R.id.image_view_small_movie_special_list);
        mImage3 = view.findViewById(R.id.image_view_sub_1_movie_special_list);
        mImage4 = view.findViewById(R.id.image_view_sub_2_movie_special_list);
        mImage5 = view.findViewById(R.id.image_view_sub_3_movie_special_list);

        switch (getArguments().getString("ARGUMENT_PAGE")) {
            case SPECIAL_MOVIE_LIST_MOST_POPULAR:
                mTextTitle.setText(getResources().getString(R.string.most_popular));
                break;
            case SPECIAL_MOVIE_LIST_NOW_PLAYING:
                mTextTitle.setText(getResources().getString(R.string.now_playing));
                break;
            case SPECIAL_MOVIE_LIST_TOP_RATE:
                mTextTitle.setText(getResources().getString(R.string.upcoming));
                break;
            case SPECIAL_MOVIE_LIST_UPCOMING:
                mTextTitle.setText(getResources().getString(R.string.top_rate));
                break;
        }
    }

    @Override
    public void showMovieSpecialListSuccess(List<Movie> movies) {
        Log.d("CAC", "showMovieSpecialListSuccess: " + movies.toString());
        mTextHeading.setText(movies.get(0).getName());
        mTextDescription.setText(movies.get(0).getOverview());
        mTextSubHeading1.setText(movies.get(1).getName());
        mTextSubHeading2.setText(movies.get(2).getName());
        mTextSubHeading3.setText(movies.get(3).getName());

        Glide.with(getContext()).load("data:image/jpeg;base64,")
                .into(mImage1);
        Glide.with(getContext()).load("data:image/jpeg;base64,")
                .into(mImage2);
        Glide.with(getContext()).load("data:image/jpeg;base64,")
                .into(mImage3);
        Glide.with(getContext()).load("data:image/jpeg;base64,")
                .into(mImage4);
        Glide.with(getContext()).load("data:image/jpeg;base64,")
                .into(mImage5);
    }

    @Override
    public void showMovieSpecialListFailed() {

    }
}
